<script id="fsc-api"
        src="https://d1f8f9xcsvx3ha.cloudfront.net/sbl/0.7.1/fastspring-builder.min.js" type="text/javascript"
        data-storefront="backlinkcontrol<?= auth()->id() == 1442?'.test':''?>.onfastspring.com/popup-backlinkcontrol"
        data-popup-closed="onFSPopupClosed"
        data-data-callback="dataCallbackFunction"
        data-error-callback="errorCallback"
>
</script>

<script>
    var fscSessiontest = { //fscSession
        'reset': true,
        'products' : [],
        'country' : null,
        'checkout': true
    };

    let products = {!! $json !!};

    function onFSPopupClosed(orderReference) {
        if (orderReference) {
            fastspring.builder.reset();
            setTimeout(function(){window.location.replace("/dashboard")}, 2000);
        }
    }

    $(function() {
        let $link = $('.link');
        let $body = $('body');
        $link.on('click',function(){
        	var $this = $(this);
        	if($this.is('.disabled')) return;
            $link.addClass('disabled').removeClass('active');
            $this.addClass('active');
            history.pushState('', '', '/products/'+$this.data('product'));
            $.ajax({
                type: 'POST',
                url: location.protocol+'//'+location.host+'/products/',
                data: { view: $this.data('product'), products: products}
            }).done(function( data ) {
                $('section.story').html(data);
                $link.removeClass('disabled');
            });

        });

        $body.on('click', '.select', function(){
            var fn = $(this);

            $('#product-id').val(fn.data('id')).data('product', fn.data('product')); // set product_id
            $('#product-details').slideUp();
            $('#order-details').slideDown();

            return false;
        });

        $body.on('click', '.cancel', function(){
            $('#order-details').slideUp();
            $('#product-details').slideDown();

            return false;
        });

        $body.on('submit', '#product-to-cart', function(e) {
            e.preventDefault();
            let $this = $(this);
            let tags = {'referrer' : app.user.id, 'campaign_id' : $('#campaign-id').val(), 'scenario': 'link_building'};
            fscSessiontest.products = [];

            fscSessiontest.products.push({'path' : $('#product-id').data('product'), 'quantity': 1});

            let requireds = $this.find(":checked");
            requireds.each(function(index){
                let required = requireds.eq(index);
                fscSessiontest.products.push({'path' : required.data('product'), 'quantity': 1});
            });

            let add_info = $this.find(".add_info");
            add_info.each(function(index){
                let required = add_info.eq(index);
                tags[required.attr('name')] = required.val();
            });

            fscSessiontest.tags = tags;
            fastspring.builder.push(fscSessiontest);

        });
    });
</script>
